<style scoped lang="scss">
ul {
    align-items: center;
    position: absolute;
    width: 100%;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);

    .logo {
        width: 600px;

        &:hover {
            cursor: pointer;
        }
    }

    li:not(.logo) {
        width: 100%;
        margin-bottom: 3rem;
        margin: 3rem 0;


        .animation {
            width: 100%;
        }

        .anchor-animation {
            display: inline-block;
            position: relative;
            color: purple;
        }

        .anchor-animation::after {
            content: '';
            position: absolute;
            width: 100%;
            transform: scaleX(0);
            height: 2px;
            bottom: 0;
            left: 0;
            background-color: purple;
            transform-origin: bottom right;
            transition: transform 0.4s ease-out;
        }

        .anchor-animation:hover::after {
            transform: scaleX(1);
            transform-origin: bottom left;
        }

        .portal {
            position: relative;
        }

        img {
            width: 250px;
        }

        img.shuttle {
            position: relative;
            animation: shuttle 3s ease-in;
        }

        .animate__bounceOut {
            animation-delay: 3.2s;
        }

        div {
            div.anchor {
                color: purple;
                position: absolute;
                left: 50%;
                transform: translate(-50%, -50%);
                font-size: 40px;

                &:hover {
                    cursor: pointer;
                }
            }
        }
    }
}


